        elif grid[i][j] == 'T':
            goal = (i, j)

for i in range(N):
    medicines[i][0] -= 1
    medicines[i][1] -= 1

queue = deque([(start, 0)])
visited = {(start, 0)}

while queue:
    (x, y), energy = queue.popleft()
    if (x, y) == goal:
        print('Yes')
        exit()
    for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
        nx, ny = x + dx, y + dy
        if 0 <= nx < H and 0 <= ny < W and grid[nx][ny] != '#':
            nenergy = energy - 1
            if nenergy < 0:
                continue
            for mx, my, me in medicines:
                if (nx, ny) == (mx, my):
                    nenergy = me
                    break
            if (nx, ny), nenergy not in visited:
                queue.append(((nx, ny), nenergy))
                visited.add(((nx, ny), nenergy))

print('No')
