                if i == 0 or row[i-1] != color:
                    return True
    # Check columns
    for col in range(3):
        for i in range(2):
            if grid[i][col] == color and grid[i+1][col] == color:
                if i == 0 or grid[i-1][col] != color:
                    return True
    # Check diagonals
    if grid[0][0] == color and grid[1][1] == color and grid[2][2] == color:
        return True
    if grid[0][2] == color and grid[1][1] == color and grid[2][0] == color:
        return True
    return False

# Define a function to get the score of a player
def get_score(grid, color):
    score = 0
    for i in range(3):
        for j in range(3):
            if grid[i][j] > 0 and (i, j) not in red_cells:
                score += grid[i][j]
    return score

# Initialize variables
red_cells = set()
blue_cells = set()
turn = 0

# Game loop
while True:
    # Takahashi's turn
    if turn % 2 == 0:
        max_score = -float('inf')
        best_cell = None
        for i in range(3):
            for j in range(3):
                if (i, j) not in red_cells and (i, j) not in blue_cells:
                    score = grid[i][j]
                    red_cells.add((i, j))
                    if has_win([[1 if (x, y) in red_cells else -1 if (x, y) in blue_cells else 0 for y in range(3)] for x in range(3)], 1):
                        red_cells.remove((i, j))
                        break
                    else:
                        red_cells.remove((i, j))
                        if score > max_score:
                            max_score = score
                            best_cell = (i, j)
        red_cells.add(best_cell)
    # Aoki's turn
    else:
        max_score = -float('inf')
        best_cell = None
        for i in range(3):
            for j in range(3):
                if (i, j) not in red_cells and (i, j) not in blue_cells:
                    score = grid[i][j]
                    blue_cells.add((i, j))
                    if has_win([[1 if (x, y) in red_cells else -1 if (x, y) in blue_cells else 0 for y in range(3)] for x in range(3)], -1):
                        blue_cells.remove((i, j))
                        break
                    else:
                        blue_cells.remove((i, j))
                        if score > max_score:
                            max_score = score
                            best_cell = (i, j)
        blue_cells.add(best_cell)
    # Check for win
    if has_win([[1 if (x, y) in red_cells else -1 if (x, y) in blue_cells else 0 for y in range(3)] for x in range(3)], 1):
