import sys

# Read input from stdin
grid = [list(map(int, input().split())) for _ in range(3)]

# Define the winning conditions
def has_won(player, grid):
    for i in range(3):
        if all(grid[i][j] > 0 if player == 'Takahashi' else grid[i][j] < 0 for j in range(3)):
            return True
        if all(grid[j][i] > 0 if player == 'Takahashi' else grid[j][i] < 0 for j in range(3)):
            return True
    if all(grid[i][i] > 0 if player == 'Takahashi' else grid[i][i] < 0 for i in range(3)):
        return True
    if all(grid[i][2-i] > 0 if player == 'Takahashi' else grid[i][2-i] < 0 for i in range(3)):
        return True
    return False

# Define the minimax function
def minimax(player, grid, depth, alpha, beta):
    if has_won('Takahashi', grid):
        return 1
    if has_won('Aoki', grid):
        return -1
    if all(all(cell != 0 for cell in row) for row in grid):
        return sum(sum(row) for row in grid)

    if player == 'Takahashi':
        max_eval = -float('inf')
        for i in range(3):
            for j in range(3):
                if grid[i][j] == 0:
                    grid[i][j] = 1
                    eval = minimax('Aoki', grid, depth + 1, alpha, beta)
                    grid[i][j] = 0
                    max_eval = max(max_eval, eval)
                    alpha = max(alpha, eval)
                    if beta <= alpha:
                        break
        return max_eval
    else:
        min_eval = float('inf')
        for i in range(3):
            for j in range(3):
                if grid[i][j] == 0:
                    grid[i][j] = -1
                    eval = minimax('Takahashi', grid, depth + 1, alpha, beta)
                    grid[i][j] = 0
                    min_eval = min(min_eval, eval)
                    beta = min(beta, eval)
                    if beta <= alpha:
                        break
        return min_eval

# Run the minimax algorithm
result = minimax('Takahashi', grid, 0, -float('inf'), float('inf'))

# Print the result
if result > 0:
    print("Takahashi")
else:
    print("Aoki")
