import sys

# Read input from stdin
grid = [list(map(int, input().split())) for _ in range(3)]

# Define a function to check if there's a win
def has_win(grid, color):
    # Check rows
    for row in grid:
        for i in range(2):
            if row[i] == color and row[i+1] == color:
                if i == 0 or row[i-1] != color:
                    return True
    # Check columns
    for col in range(3):
        for i in range(2):
            if grid[i][col] == color and grid[i+1][col] == color:
                if i == 0 or grid[i-1][col] != color:
                    return True
    # Check diagonals
    if grid[0][0] == color and grid[1][1] == color and grid[2][2] == color:
        return True
    if grid[0][2] == color and grid[1][1] == color and grid[2][0] == color:
        return True
    return False

# Define a function to get the score of a player
def get_score(grid, color):
    score = 0
    for i in range(3):
        for j in range(3):
            if grid[i][j] > 0 and (i, j) in [(x, y) for x in range(3) for y in range(3) if (x, y) == (i, j) and grid[x][y] == color]:
                score += grid[i][j]
    return score

# Define a function to make a move
def make_move(grid, i, j, color):
    grid[i][j] = color
    return grid

# Define a function to undo a move
def undo_move(grid, i, j):
    grid[i][j] = -grid[i][j]
    return grid

# Define a function to get the next moves
def get_next_moves(grid):
    next_moves = []
    for i in range(3):
        for j in range(3):
            if grid[i][j] > 0:
                next_moves.append((i, j))
    return next_moves

# Define a function to check if the game is over
def is_game_over(grid):
    if has_win(grid, -1):
        return True
    if has_win(grid, 1):
        return True
    for i in range(3):
        for j in range(3):
            if grid[i][j] > 0:
                return False
    return True

# Define a function to get the winner
def get_winner(grid):
    if has_win(grid, -1):
        return "Aoki"
    if has_win(grid, 1):
        return "Takahashi"
    if get_score(grid, -1) > get_score(grid, 1):
        return "Aoki"
    return "Takahashi"

# Define a function to play the game
def play_game(grid):
    if is_game_over(grid):
        return get_winner(grid)
    next_moves = get_next_moves(grid)
    best_move = None
    best_outcome = None
    for move in next_moves:
        new_grid = make_move(grid, move[0], move[1], -1)
        outcome = play_game(new_grid)
        undo_move(new_grid, move[0], move[1])
        if best_outcome is None or outcome == "Takahashi":
            best_move = move
            best_outcome = outcome
    new_grid = make_move(grid, best_move[0], best_move[1], 1)
    return play_game(new_grid)

# Play the game and print the winner
print(play_game(grid))
