            return True
    if all(grid[i][i] == player for i in range(3)):
        return True
    if all(grid[i][2-i] == player for i in range(3)):
        return True
    return False

# Define the minimax function
def minimax(grid, turn, alpha, beta):
    if has_won('R', grid):
        return 1
    if has_won('B', grid):
        return -1
    if all(cell != 0 for row in grid for cell in row):
        return 0

    if turn == 'T':
        for i in range(3):
            for j in range(3):
                if grid[i][j] == 0:
                    grid[i][j] = 'R'
                    score = minimax(grid, 'A', alpha, beta)
                    grid[i][j] = 0
                    alpha = max(alpha, score)
                    if alpha >= beta:
                        return alpha
        return alpha
    else:
        for i in range(3):
            for j in range(3):
                if grid[i][j] == 0:
                    grid[i][j] = 'B'
                    score = minimax(grid, 'T', alpha, beta)
                    grid[i][j] = 0
                    beta = min(beta, score)
                    if alpha >= beta:
                        return beta
        return beta

# Run the minimax algorithm
