import sys
from heapq import heapify, heappop

def read_input():
    N, M = map(int, sys.stdin.readline().split())
    edges = []
    for _ in range(M):
        K, C = map(int, sys.stdin.readline().split())
        vertices = list(map(int, sys.stdin.readline().split()))
        for i in range(K):
            for j in range(i + 1, K):
                edges.append((C, vertices[i], vertices[j]))
    return N, edges

def kruskal(N, edges):
    parent = list(range(N + 1))
    rank = [0] * (N + 1)

    def find(u):
        if parent[u] != u:
            parent[u] = find(parent[u])
        return parent[u]

    def union(u, v):
        root1 = find(u)
        root2 = find(v)
        if root1 != root2:
            if rank[root1] > rank[root2]:
                parent[root2] = root1
            else:
                parent[root1] = root2
                if rank[root1] == rank[root2]:
                    rank[root2] += 1

    heapify(edges)
    mst_weight = 0
    while edges:
        C, u, v = heappop(edges)
        if find(u) != find(v):
            union(u, v)
            mst_weight += C

    connected = True
    for i in range(1, N + 1):
        if find(i) != find(1):
            connected = False
            break

    if connected:
        return mst_weight
    else:
        return -1

N, edges = read_input()
print(kruskal(N, edges))
```

