def count_black_cells(A, B, C, D):
    def is_black(x, y):
        return (x + y) % 2 == (x % 2) ^ (y % 2)

    black_cells = 0
    for x in range(A, C):
        for y in range(B, D):
            if is_black(x, y):
                black_cells += 1
    return black_cells * 2

