def can_win(removed):
    for a in front:
        if len(front[a]) > len(removed.get(a, [])):
            return False
    for b in back:
        if len(back[b]) > len(removed.get(b, [])):
            return False
    return True

for r in range(N, -1, -1):
    for removed in combinations(range(N), r):
        removed = {i: [] for i in range(N)}
        for i in removed:
            a, b = map(int, input().split())
            removed[a].append(b)
            removed[b].append(a)
        if can_win(removed):
            print("Takahashi" if r % 2 == 0 else "Aoki")
            exit()
