import sys

N, L, R = map(int, sys.stdin.readline().split())

def ask(i, j):
    print(f"? {i} {j}")
    sys.stdout.flush()
    return int(sys.stdin.readline())

def solve(L, R):
    if L == R:
        return ask(0, L)
    i = 0
    while (1 << i) < R - L + 1:
        i += 1
    j = L >> (i - 1)
    mid = (1 << (i - 1)) * (j + 1) - 1
    if L <= mid:
        left = solve(L, min(R, mid))
        if mid < R:
            right = solve(mid + 1, R)
            return (left + right) % 100
        return left
    return solve(L, R)

result = solve(L, R)
print(f"! {result}")
sys.stdout.flush()
