def print_carpet(n):
    carpet = [['#' for _ in range(3**n)] for _ in range(3**n)]
    def fill_carpet(x, y, size):
        if size == 1:
            return
        gap = size // 3
        for i in range(gap, 2*gap):
            for j in range(gap, 2*gap):
                carpet[x+i][y+j] = '.'
        for i in range(0, size, gap):
            for j in range(0, size, gap):
                if i != gap or j != gap:
                    fill_carpet(x+i, y+j, gap)
    fill_carpet(0, 0, 3**n)
    for row in carpet:
        print(''.join(row))

