def carpet(N):
    if N == 0:
        return [['#']]
    else:
        smaller_carpet = carpet(N-1)
        size = 3 ** N
        result = [['.' for _ in range(size)] for _ in range(size)]
        block_size = 3 ** (N-1)
        for i in range(3):
            for j in range(3):
                if i == 1 and j == 1:
                    continue
                for x in range(block_size):
                    for y in range(block_size):
                        result[i*block_size+x][j*block_size+y] = smaller_carpet[x][y]
        return result

