def carpet(N):
    if N == 0:
        return [['#']]
    else:
        smaller_carpet = carpet(N-1)
        size = 3 ** N
        result = [['.' for _ in range(size)] for _ in range(size)]
        for i in range(size):
            for j in range(size):
                if (i // 3 ** (N-1)) % 3 == 1 and (j // 3 ** (N-1)) % 3 == 1:
                    continue
                result[i][j] = smaller_carpet[i // 3 ** (N-1) % 3 ** (N-1)][j // 3 ** (N-1) % 3 ** (N-1)]
        return result

