```python
import heapq
import sys

N, M = map(int, sys.stdin.readline().split())
A = list(map(int, sys.stdin.readline().split()))
edges = []
for _ in range(M):
    u, v, b = map(int, sys.stdin.readline().split())
    edges.append((b, u, v))

edges.sort()
parent = list(range(N + 1))
rank = [0] * (N + 1)

def find(u):
    if parent[u] != u:
        parent[u] = find(parent[u])
    return parent[u]

def union(u, v):
    root_u = find(u)
    root_v = find(v)
    if root_u != root_v:
        if rank[root_u] > rank[root_v]:
            parent[root_v] = root_u
        else:
            parent[root_u] = root_v
            if rank[root_u] == rank[root_v]:
                rank[root_v] += 1

mst_edges = []
for b, u, v in edges:
    if find(u) != find(v):
        mst_edges.append((b, u, v))
        union(u, v)

graph = [[] for _ in range(N + 1)]
for b, u, v in mst_edges:
    graph[u].append((b, v))
    graph[v].append((b, u))

dist = [float('inf')] * (N + 1)
dist[1] = A[0]
pq = [(A[0], 1)]

while pq:
    d, u = heapq.heappop(pq)
    if d > dist[u]:
        continue
    for b, v in graph[u]:
        if dist[u] + b + A[v - 1] < dist[v]:
            dist[v] = dist[u] + b + A[v - 1]
            heapq.heappush(pq, (dist[v], v))

print(' '.join(map(str, dist[2:])))
```

