from collections import deque

H, W, Y = map(int, input().split())
A = [list(map(int, input().split())) for _ in range(H)]

def bfs(year):
    queue = deque()
    for i in range(H):
        for j in range(W):
            if A[i][j] <= year:
                queue.append((i, j))
                A[i][j] = -1
    while queue:
        x, y = queue.popleft()
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < H and 0 <= ny < W and A[nx][ny] <= year:
                queue.append((nx, ny))
                A[nx][ny] = -1

for year in range(1, Y + 1):
    land = sum(1 for row in A for cell in row if cell != -1)
    print(land)
    bfs(year)
```

