from functools import lru_cache

N, X, Y = map(int, input().split())
dishes = [list(map(int, input().split())) for _ in range(N)]

@lru_cache(None)
def dp(i, x, y):
    if i == N:
        return 0
    eat = 0 if x + dishes[i][0] > X or y + dishes[i][1] > Y else 1 + dp(i + 1, x + dishes[i][0], y + dishes[i][1])
    not_eat = dp(i + 1, x, y)
    return max(eat, not_eat)

print(dp(0, 0, 0))
```

