import sys
from heapq import heapify, heappop

N, Q = map(int, sys.stdin.readline().split())
edges = []
for _ in range(Q):
    L, R, C = map(int, sys.stdin.readline().split())
    for j in range(L, R + 1):
        edges.append((C, N + _, j))

edges.sort()
parent = list(range(N + Q + 1))
rank = [0] * (N + Q + 1)

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    x_root = find(x)
    y_root = find(y)
    if x_root != y_root:
        if rank[x_root] < rank[y_root]:
            parent[x_root] = y_root
        elif rank[x_root] > rank[y_root]:
            parent[y_root] = x_root
        else:
            parent[y_root] = x_root
            rank[x_root] += 1

heapify(edges)
cost = 0
connected_components = N + Q
while edges and connected_components > 1:
    c, u, v = heappop(edges)
    if find(u) != find(v):
        union(u, v)
        cost += c
        connected_components -= 1

if connected_components == 1:
    sys.stdout.write(str(cost) + '\n')
else:
    sys.stdout.write('-1\n')
```

