import sys
import heapq

def main():
    N, Q = map(int, sys.stdin.readline().split())
    edges = []
    for i in range(Q):
        L, R, C = map(int, sys.stdin.readline().split())
        for j in range(L, R + 1):
            edges.append((C, N + i + 1, j))
    edges.sort()

    parent = list(range(N + Q + 1))
    rank = [0] * (N + Q + 1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
