def xor_sum(a, k, mod):
    res = 0
    for i in range(1, 1 << len(a)):
        xor_val = 0
        for j in range(len(a)):
            if (i & (1 << j)):
                xor_val ^= a[j]
        if bin(i).count('1') % k == 0:
            res += pow(xor_val, k, mod)
    return res % mod

