        res ^= x
    return pow(res, k, 998244353)

def solve(n, m, k, a):
    MOD = 998244353
    dp = [[0] * (m + 1) for _ in range(n + 1)]
    dp[0][0] = 1
    for i in range(n):
        for j in range(m):
            dp[i + 1][(j + 1) % m] += dp[i][j]
            dp[i + 1][j] += dp[i][j]
            dp[i + 1][j] %= MOD
    res = 0
    for i in range(1, n + 1):
        if i % m == 0:
            res += dp[n][m] * xor_and_power(a[:i], k)
            res %= MOD
    return res

n, m, k = map(int, input().split())
a = list(map(int, input().split()))
print(solve(n, m, k, a))
