def main():
    N, K = map(int, sys.stdin.readline().split())
    graph = defaultdict(list)
    for _ in range(N - 1):
        A, B = map(int, sys.stdin.readline().split())
        graph[A].append(B)
        graph[B].append(A)
    V = list(map(int, sys.stdin.readline().split()))

    parent = [-1] * (N + 1)
    depth = [0] * (N + 1)
    dfs(1, 0, graph, parent, depth)

    LCA = [[-1] * (N + 1) for _ in range(20)]
    for i in range(N + 1):
        LCA[0][i] = parent[i]
    for j in range(1, 20):
        for i in range(N + 1):
            if LCA[j - 1][i] != -1:
                LCA[j][i] = LCA[j - 1][LCA[j - 1][i]]

    def get_lca(u, v):
        if depth[u] < depth[v]:
            u, v = v, u
        diff = depth[u] - depth[v]
        for i in range(20):
            if (diff >> i) & 1:
                u = LCA[i][u]
        if u == v:
            return u
        for i in range(19, -1, -1):
            if LCA[i][u] != LCA[i][v]:
                u, v = LCA[i][u], LCA[i][v]
        return LCA[0][u]

    min_depth = float('inf')
    lca = -1
    for v in V:
        if depth[v] < min_depth:
            min_depth = depth[v]
            lca = v
        elif depth[v] == min_depth:
            lca = get_lca(lca, v)

    ans = 1
    for v in V:
        ans += depth[v] - depth[lca]

    print(ans)

def dfs(u, d, graph, parent, depth):
    depth[u] = d
    for v in graph[u]:
        if parent[u] != v:
            parent[v] = u
            dfs(v, d + 1, graph, parent, depth)

if __name__ == "__main__":
    main()
