for i in range(N):
    dp[i][i] = 1

for length in range(1, N):
    for i in range(N - length):
        j = i + length
        if length == 1:
            dp[i][j] = 1
        elif A[j] - A[j - 1] == A[j - 1] - A[j - 2]:
            dp[i][j] = dp[i][j - 1] + 1
        else:
            dp[i][j] = dp[i][j - 1]

