import sys

def dijkstra(graph, start, required_bridges):
    queue = [(0, start, set())]
    distances = {start: 0}
    while queue:
        dist, node, bridges = heapq.heappop(queue)
        if node == N:
            if required_bridges.issubset(bridges):
                return dist
        for neighbor, bridge, time in graph[node]:
            if bridge not in bridges:
                new_dist = dist + time
                if neighbor not in distances or new_dist < distances[neighbor]:
                    distances[neighbor] = new_dist
                    heapq.heappush(queue, (new_dist, neighbor, bridges | {bridge}))
    return -1

