import sys
from collections import defaultdict

def main():
    N = int(sys.stdin.readline())
    edges = []
    for _ in range(N - 1):
        u, v, l = map(int, sys.stdin.readline().split())
        edges.append((l, u, v))
    edges.sort()

    parent = list(range(N + 1))
    rank = [0] * (N + 1)

    def find(x):
        if parent[x] != x:
            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            if rank[root_x] > rank[root_y]:
                parent[root_y] = root_x
            else:
                parent[root_x] = root_y
                if rank[root_x] == rank[root_y]:
                    rank[root_y] += 1

    mst = []
    for l, u, v in edges:
        if find(u) != find(v):
            mst.append((l, u, v))
            union(u, v)

    dp = [0] * (N + 1)
    for l, u, v in mst:
        dp[u] += l
        dp[v] += l

    ans = [0] * (N + 1)
    for i in range(1, N + 1):
        ans[i] = min(ans[i - 1] + dp[i], ans[i - 1] * 2)

    for i in range(1, N + 1):
        print(ans[i])

if __name__ == "__main__":
    main()
