MOD = 998244353
MAX_N = 10000001

def sieve_of_eratosthenes(n):
    primes = [True] * (n + 1)
    primes[0] = primes[1] = False
    p = 2
    while p * p <= n:
        if primes[p]:
            for i in range(p * p, n + 1, p):
                primes[i] = False
        p += 1
    return primes

def count_good_numbers(n):
    good_numbers = [False] * (n + 1)
    good_numbers[0] = good_numbers[1] = True
    for i in range(2, n + 1):
        sum_divisors = 1
        for j in range(2, int(i ** 0.5) + 1):
            if i % j == 0:
                sum_divisors += j
                if j * j != i:
                    sum_divisors += i // j
        good_numbers[i] = sum_divisors % 3 == 0
    return good_numbers

def count_sequences(n, m, good_numbers):
    dp = [0] * (n + 1)
    dp[0] = 1
    for i in range(1, n + 1):
        if good_numbers[i]:
            for j in range(i, n + 1):
                dp[j] += dp[j - i]
                dp[j] %= MOD
    return dp[n]

