MOD = 998244353
MAX_N = 10000001

def sieve_of_eratosthenes(n):
    prime = [True] * (n + 1)
    prime[0] = prime[1] = False
    p = 2
    while p ** 2 <= n:
        if prime[p]:
            for i in range(p ** 2, n + 1, p):
                prime[i] = False
        p += 1
    return prime

def count_good_numbers(n):
    prime = sieve_of_eratosthenes(n)
    dp = [0] * (n + 1)
    dp[0] = 1
    for p in range(2, n + 1):
        if prime[p]:
            for i in range(p, n + 1):
                dp[i] += dp[i - p]
                dp[i] %= MOD
    good = [0] * (n + 1)
    for i in range(1, n + 1):
        s = sum(j for j in range(1, i + 1) if i % j == 0)
        if s % 3 == 0:
            good[i] = 1
    ans = 0
