    edges_p_H = set()
    for a, b in edges_H:
        edges_p_H.add(tuple(sorted((p[a - 1], p[b - 1]))))
    cost = 0
    for i in range(N):
        for j in range(i + 1, N):
            if (i + 1, j + 1) in edges_G:
                if (p[i], p[j]) not in edges_p_H:
                    cost += A[i][j - 1]
            else:
                if (p[i], p[j]) in edges_p_H:
                    cost += A[i][j - 1]
    min_cost = min(min_cost, cost)

