from functools import lru_cache

@lru_cache(None)
def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

@lru_cache(None)
def lcm(a, b):
    return a * b // gcd(a, b)

def solve(A, B, C):
    x, y = 1, 1
    for a, b, c in zip(A, B, C):
        x = lcm(x, a)
        y = lcm(y, b)
        g = gcd(x, y)
        x //= g
        y //= g
        if c % x != 0 or c % y != 0:
            return 0
    return (c // x) * (c // y)

T = int(input())
for _ in range(T):
    N = int(input())
    A, B, C = [], [], []
    for _ in range(N):
        a, b, c = map(int, input().split())
        A.append(a)
        B.append(b)
        C.append(c)
    print(solve(tuple(A), tuple(B), tuple(C)))
