    count = defaultdict(int)
    for a, b in zip(A, B):
        if a != b:
            count[a] += 1
    for a in A:
        if count[a] > 0:
            for j in range(max(0, a-K), min(a+K+1, len(A))):
                if A[j] == a:
                    count[a] -= 1
                    break
    return all(v == 0 for v in count.values())

