import sys

N = int(sys.stdin.readline())
edges = [list(map(int, sys.stdin.readline().split())) for _ in range(N - 1)]

parent = list(range(N + 1))
rank = [0] * (N + 1)

def find(x):
    if parent[x] != x:
        parent[x] = find(parent[x])
    return parent[x]

def union(x, y):
    x_root = find(x)
    y_root = find(y)
    if x_root != y_root:
        if rank[x_root] < rank[y_root]:
            parent[x_root] = y_root
        elif rank[x_root] > rank[y_root]:
            parent[y_root] = x_root
        else:
            parent[y_root] = x_root
            rank[x_root] += 1

for a, b in edges:
    union(a, b)

groups = {}
for i in range(1, N + 1):
    group = find(i)
    if group not in groups:
        groups[group] = []
    groups[group].append(i)

ans = []
for _ in range(N // 2):
    max_dist = 0
    max_pair = None
    for group in groups.values():
        if len(group) >= 2:
            for i in range(len(group)):
                for j in range(i + 1, len(group)):
                    dist = abs(group[i] - group[j]) // 2
                    if dist > max_dist:
                        max_dist = dist
                        max_pair = (group[i], group[j])
    if max_pair:
        ans.append(max_pair)
        groups[find(max_pair[0])].remove(max_pair[0])
        groups[find(max_pair[1])].remove(max_pair[1])

for x, y in ans:
    print(x, y)
