class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        from collections import Counter
        count1, count2 = Counter(nums1), Counter(nums2)
        res = 0
        for k in set(nums1 + nums2):
            res += min(count1[k], count2[k])
        return res
