class Solution:
    def maximumSetSize(self, nums1: List[int], nums2: List[int]) -> int:
        count1 = {}
        count2 = {}

        for num in nums1:
            if num in count1:
                count1[num] += 1
            else:
                count1[num] = 1

        for num in nums2:
            if num in count2:
                count2[num] += 1
            else:
                count2[num] = 1

        count = {}
        for num in count1:
            if num in count2:
                count[num] = min(count1[num], count2[num])
            else:
                count[num] = count1[num]

        for num in count2:
            if num not in count:
                count[num] = count2[num]

        total = len(nums1) + len(nums2)
        removed = 0
        size = 0
        for num in sorted(count, key=count.get, reverse=True):
            while count[num] > 0 and removed < total // 2:
                removed += 1
                count[num] -= 1
                size += 1
                if count[num] == 0:
                    del count[num]
                    break

        return size
