class Solution:
    def minOperations(self, nums: List[int], k: int) -> int:
        xor = 0
        for num in nums:
            xor ^= num
        if xor == k:
            return 0
        res = float('inf')
        for num in nums:
            new_xor = xor ^ num
            cnt = 0
            while new_xor != k:
                cnt += 1
                new_xor ^= (new_xor & -new_xor)
            res = min(res, cnt)
        return res
