class Solution:
    def minOrAfterOperations(self, nums: List[int], k: int) -> int:
        n = len(nums)
        res = nums[0]
        for i in range(1, n):
            res |= nums[i]
        while k > 0:
            new_nums = []
            i = 0
            while i < n:
                if i + 1 < n and k > 0:
                    new_nums.append(nums[i] & nums[i + 1])
                    i += 2
                    k -= 1
                else:
                    new_nums.append(nums[i])
                    i += 1
            nums = new_nums
            n = len(nums)
            res = min(res, reduce(or_, nums))
        return res
