    def minimumCost(self, nums: List[int]) -> int:
        n = len(nums)
        prefix = [0] * (n + 1)
        for i in range(n):
            prefix[i + 1] = prefix[i] + nums[i]

        dp = [[[float('inf')] * (n + 1) for _ in range(4)] for _ in range(n + 1)]
        dp[0][0][0] = 0

        for i in range(1, n + 1):
