class Solution:
    def maxPalindromesAfterOperations(self, words: List[str]) -> int:
        count = [{} for _ in range(26)]
        for word in words:
            freq = {}
            for char in word:
                freq[ord(char) - ord('a')] += 1
            for key in freq:
                count[key][freq[key]] = count[key].get(freq[key], 0) + 1
        res = 0
        for i in range(26):
            for j in range(25, -1, -1):
                if j % 2 == 0:
                    res += count[i].get(j, 0)
                else:
                    res += count[i].get(j, 0) // 2 * 2
        return res
