    def longestCommonPrefix(self, arr1: List[int], arr2: List[int]) -> int:
        def common_prefix(a, b):
            while b:
                if a < b:
                    b //= 10
                elif a > b:
                    a //= 10
                else:
                    break
            return len(str(a))

        max_prefix = 0
        for x in arr1:
            for y in arr2:
                max_prefix = max(max_prefix, common_prefix(x, y))
        return max_prefix

class Solution:
    def longestCommonPrefix(self, arr1: List[int], arr2: List[int]) -> int:
        def common_prefix(a, b):
            while a and b:
                if a % 10 == b % 10:
                    a //= 10
                    b //= 10
                else:
                    break
            return len(str(a)) if a else 0

        max_prefix = 0
        for x in arr1:
            for y in arr2:
                max_prefix = max(max_prefix, common_prefix(x, y))
        return max_prefix

