from collections import deque

H, W, K = map(int, input().split())
grid = [input() for _ in range(H)]

def bfs():
    q = deque([(i, j) for i in range(H) for j in range(W) if grid[i][j] == 'o'])
    visited = {(i, j) for i, j in q}
    for _ in range(K):
        q2 = deque()
        for i, j in q:
            for x, y in [(i-1, j), (i+1, j), (i, j-1), (i, j+1)]:
                if 0 <= x < H and 0 <= y < W and grid[x][y] == '.' and (x, y) not in visited:
                    q2.append((x, y))
                    visited.add((x, y))
        q = q2
    return len(visited)

def check_horizontal():
    for i in range(H):
        count = 0
        for j in range(W):
            if grid[i][j] == 'o':
                count += 1
            else:
                count = 0
            if count == K:
                return True
    return False

def check_vertical():
    for j in range(W):
        count = 0
        for i in range(H):
            if grid[i][j] == 'o':
                count += 1
            else:
                count = 0
            if count == K:
                return True
    return False

if check_horizontal() or check_vertical():
    print(0)
else:
    res = bfs()
    if res >= K:
        print(-1)
    else:
        print(res - K + 1)
