from collections import deque

def bellman_ford(graph, start):
    n = len(graph)
    dist = [float('inf')] * n
    dist[start - 1] = 0
    for _ in range(n - 1):
        for u, v, w in graph:
            if dist[u - 1] != float('inf') and dist[u - 1] + w < dist[v - 1]:
                dist[v - 1] = dist[u - 1] + w
    return dist

def bfs(graph, start):
    n = len(graph)
    visited = [False] * n
    queue = deque([start])
    visited[start - 1] = True
    while queue:
        u = queue.popleft()
        for v, _ in [(v, w) for u_, v, w in graph if u_ == u]:
            if not visited[v - 1]:
                queue.append(v)
                visited[v - 1] = True
    return all(visited)

