from collections import defaultdict, deque

def bellman_ford(graph, start):
    distance = {node: float('inf') for node in graph}
    distance[start] = 0
    for _ in range(len(graph) - 1):
        for u in graph:
            for v, w in graph[u]:
                distance[v] = min(distance[v], distance[u] + w)
    return distance

def has_negative_cycle(graph):
    distance = {node: 0 for node in graph}
    for _ in range(len(graph) - 1):
        for u in graph:
            for v, w in graph[u]:
                if distance[u] + w < distance[v]:
                    distance[v] = distance[u] + w
    for u in graph:
        for v, w in graph[u]:
            if distance[u] + w < distance[v]:
                return True
    return False

def topological_sort(graph):
    in_degree = {node: 0 for node in graph}
    for u in graph:
        for v, _ in graph[u]:
            in_degree[v] += 1
    queue = deque([node for node in graph if in_degree[node] == 0])
    order = []
    while queue:
        u = queue.popleft()
        order.append(u)
        for v, _ in graph[u]:
            in_degree[v] -= 1
            if in_degree[v] == 0:
                queue.append(v)
    return order

def main():
    N, M = map(int, input().split())
    graph = defaultdict(list)
    for _ in range(M):
        u, v, w = map(int, input().split())
        graph[u].append((v, w))
    if has_negative_cycle(graph):
        print("No")
        return
    order = topological_sort(graph)
    if len(order) != N:
        print("No")
        return
    distance = bellman_ford(graph, order[0])
    min_weight = sum(distance.values())
    print(min_weight)

if __name__ == "__main__":
    main()

