trains = [[] for _ in range(N + 1)]
for _ in range(M):
    l, d, k, c, A, B = map(int, input().split())
    for i in range(k):
        t = l + i * d
        trains[A].append((t, c, B))

INF = 10 ** 18
dp = [INF] * (N + 1)
