    t, v = heapq.heappop(pq)
    if t > dp[v]:
        continue
    for nt, c, nv in trains[v]:
        nt += t
        if nt < dp[nv]:
            dp[nv] = nt
            heapq.heappush(pq, (nt, nv))

for i in range(1, N):
    if dp[i] == INF:
        print("Unreachable")
    else:
        print(dp[i])
