from collections import defaultdict, deque
from typing import List, Tuple

def solve() -> None:
    N, M = map(int, input().split())
    trains: List[Tuple[int, int, int, int, int, int]] = []
    for _ in range(M):
        l, d, k, c, A, B = map(int, input().split())
        trains.append((l, d, k, c, A, B))

    graph: List[List[Tuple[int, int]]] = [[] for _ in range(N + 1)]
    for l, d, k, c, A, B in trains:
        for t in range(l, l + d * (k - 1) + 1, d):
            graph[A].append((t, t + c, B))

    dp: List[int] = [-float('inf')] * (N + 1)
    dp[1] = 0
    queue: deque[int] = deque([1])

    while queue:
        node = queue.popleft()
        for t, arrival, neighbor in graph[node]:
            if arrival > dp[neighbor]:
                dp[neighbor] = arrival
                queue.append(neighbor)

    for i in range(1, N):
        if dp[i] == -float('inf'):
            print("Unreachable")
        else:
            print(dp[i])

if __name__ == "__main__":
    solve()
